"use strict";
const utils = require("../utils");
const package_json_1 = require("../../package.json");
const init = (app) => {
    if (app.disabled("x-powered-by"))
        return null;
    const xPoweredBy = utils.string.capitalize(package_json_1.name);
    return function foxify_init(req, res, next) {
        res.setHeader("X-Powered-By", xPoweredBy);
        next();
    };
};
module.exports = init;
