"use strict";
const path = require("path");
class Engine {
    constructor(path, ext, handler) {
        this._path = path;
        this._ext = ext;
        this._handler = handler;
    }
    render(filename, opts = {}, cb) {
        this._handler(path.join(this._path, `${filename}.${this._ext}`), opts, cb);
    }
}
module.exports = Engine;
